def detect_start_of_packet(datastream, marker_size):
  buffer = ""
  for i, c in enumerate(datastream):
    buffer += c
    if len(buffer) < marker_size:
      continue
    if len(set(buffer[-marker_size:])) == marker_size:
      return i - marker_size + 1
  return -1

datastream = ""
start_of_packet = detect_start_of_packet(datastream, 4)
start_of_message = detect_start_of_packet(datastream, 14)

print("Start of packet:", start_of_packet)
print("Start of message:", start_of_message)

